'use client';
import { motion, useReducedMotion } from 'framer-motion';

const HEADLINE = 'Build faster.';

export function HeroSection() {
  const reduced = useReducedMotion();

  const words = HEADLINE.split(' ');

  const wordVariants = {
    hidden: { opacity: 0, y: reduced ? 0 : 20 },
    visible: (i: number) => ({
      opacity: 1,
      y: 0,
      transition: { delay: reduced ? 0 : i * 0.12, duration: 0.4 },
    }),
  };

  const fadeIn = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { delay: reduced ? 0 : 0.6, duration: 0.5 } },
  };

  const slideUp = {
    hidden: { opacity: 0, y: reduced ? 0 : 24 },
    visible: { opacity: 1, y: 0, transition: { delay: reduced ? 0 : 0.9, duration: 0.4 } },
  };

  return (
    <section className="flex flex-col items-center justify-center min-h-[80vh] px-6 text-center">
      <h1 className="text-6xl font-bold tracking-tight mb-4" aria-label={HEADLINE}>
        {words.map((word, i) => (
          <motion.span
            key={word}
            custom={i}
            initial="hidden"
            animate="visible"
            variants={wordVariants}
            className="inline-block mr-3"
            aria-hidden
          >
            {word}
          </motion.span>
        ))}
      </h1>

      <motion.p
        initial="hidden"
        animate="visible"
        variants={fadeIn}
        className="text-xl text-gray-500 mb-8 max-w-md"
      >
        Ship with confidence.
      </motion.p>

      <motion.a
        href="/signup"
        initial="hidden"
        animate="visible"
        variants={slideUp}
        className="px-8 py-3 bg-black text-white rounded-full text-lg font-medium hover:bg-gray-800 transition-colors"
      >
        Get started
      </motion.a>
    </section>
  );
}

'use client';
import { useEffect, useRef, useState } from 'react';
import { useReducedMotion } from 'framer-motion';

type Stat = { label: string; target: number; suffix: string; decimals?: number };

const STATS: Stat[] = [
  { label: 'Users', target: 10000, suffix: '+' },
  { label: 'Uptime', target: 99.9, suffix: '%', decimals: 1 },
  { label: 'p95 Latency', target: 40, suffix: 'ms' },
];

function useCountUp(target: number, decimals = 0, active: boolean, reduced: boolean) {
  const [value, setValue] = useState(reduced || !active ? target : 0);

  useEffect(() => {
    if (!active || reduced) { setValue(target); return; }
    const duration = 1400;
    const start = performance.now();
    const raf = (now: number) => {
      const progress = Math.min((now - start) / duration, 1);
      const eased = 1 - Math.pow(1 - progress, 3);
      setValue(parseFloat((eased * target).toFixed(decimals)));
      if (progress < 1) requestAnimationFrame(raf);
    };
    requestAnimationFrame(raf);
  }, [active, target, decimals, reduced]);

  return value;
}

function StatCard({ stat }: { stat: Stat }) {
  const ref = useRef<HTMLDivElement>(null);
  const [active, setActive] = useState(false);
  const reduced = useReducedMotion() ?? false;
  const value = useCountUp(stat.target, stat.decimals ?? 0, active, reduced);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => { if (entry.isIntersecting) setActive(true); },
      { threshold: 0.4 }
    );
    if (ref.current) observer.observe(ref.current);
    return () => observer.disconnect();
  }, []);

  const formatted = stat.decimals
    ? value.toFixed(stat.decimals)
    : value >= 1000
    ? `${(value / 1000).toFixed(0)}k`
    : String(value);

  return (
    <div ref={ref} className="flex flex-col items-center gap-1">
      <span className="text-5xl font-bold tabular-nums">
        {formatted}{stat.suffix}
      </span>
      <span className="text-sm text-gray-500 uppercase tracking-widest">{stat.label}</span>
    </div>
  );
}

export function StatsSection() {
  return (
    <section className="py-20 border-t border-gray-100">
      <div className="max-w-4xl mx-auto grid grid-cols-3 gap-8 px-6">
        {STATS.map((s) => <StatCard key={s.label} stat={s} />)}
      </div>
    </section>
  );
}
